)
$ErrorActionPreference = 'Stop'

function Get-ApiKey {
  Param([string]$Inline,[string]$File)
  if ($Inline) { return $Inline.Trim('"','<','>') }
  $user = [Environment]::GetEnvironmentVariable('N8N_API_KEY','User')
  if ($user) { return $user.Trim('"','<','>') }
  if ($Env:N8N_API_KEY_FILE) { $File = $Env:N8N_API_KEY_FILE }
  $candidates = @()
  if ($File) { $candidates += ,$File }
  $repo = (Get-Location).Path
  $candidates += ,(Join-Path $repo 'n8n_api_key.txt')
  foreach ($p in $candidates) { if ($p -and (Test-Path $p)) { return (Get-Content -Raw $p).Trim('"','<','>') } }
  return ''
}

if (-not $ApiUrl) { $ApiUrl = 'http://localhost:5678' }
$ApiKey = Get-ApiKey -Inline $ApiKey -File $ApiKeyFile
if (-not $ApiKey) { Write-Error 'N8N_API_KEY not found (env or file). Set $Env:N8N_API_KEY or create n8n_api_key.txt'; exit 1 }

New-Item -ItemType Directory -Path '.specify\memory' -ea 0 | Out-Null

Write-Host "[1/5] Import workflow..." -ForegroundColor Cyan
$body = Get-Content -Raw 'n8n/workflow.webhook_sheets_email.json'
