$id | Out-File -Encoding ascii '.specify\memory\.last_n8n_workflow_id'

Write-Host "[2/5] Activate workflow $id..." -ForegroundColor Cyan
Invoke-RestMethod -Method Patch -Uri ($ApiUrl.TrimEnd('/') + '/rest/workflows/' + $id) -Headers @{ 'X-N8N-API-KEY'=$ApiKey } -ContentType 'application/json' -Body '{"active":true}' | Out-Null

Write-Host "[3/5] Update node parameters (Sheets/Email)..." -ForegroundColor Cyan
if (-not $SheetId) { $SheetId = [Environment]::GetEnvironmentVariable('SHEET_ID','User') }
if (-not $EmailFrom) { $EmailFrom = [Environment]::GetEnvironmentVariable('EMAIL_FROM','User') }
if (-not $EmailTo) { $EmailTo = [Environment]::GetEnvironmentVariable('EMAIL_TO','User') }
if (-not $SheetId) { Write-Error 'SHEET_ID not set (env or user env)'; exit 1 }
if (-not $EmailFrom) { Write-Error 'EMAIL_FROM not set'; exit 1 }
if (-not $EmailTo) { Write-Error 'EMAIL_TO not set'; exit 1 }
$wf = Invoke-RestMethod -Method Get -Uri ($ApiUrl.TrimEnd('/') + '/rest/workflows/' + $id) -Headers @{ 'X-N8N-API-KEY'=$ApiKey }
foreach ($n in $wf.nodes) {
  if ($n.name -eq 'Google Sheets: Append') { $n.parameters.sheetId = $SheetId }
  if ($n.name -eq 'Email') { $n.parameters.fromEmail = $EmailFrom; $n.parameters.toEmail = $EmailTo }
}
$patched = ($wf | ConvertTo-Json -Depth 20)
Invoke-RestMethod -Method Patch -Uri ($ApiUrl.TrimEnd('/') + '/rest/workflows/' + $id) -Headers @{ 'X-N8N-API-KEY'=$ApiKey } -ContentType 'application/json' -Body $patched | Out-Null

Write-Host "[4/5] Write webhook URL and test..." -ForegroundColor Cyan
$base = if ($Env:N8N_BASE_URL) { $Env:N8N_BASE_URL } else { $ApiUrl }
$wh = $base.TrimEnd('/') + '/webhook/tw-live-signal'
$wh | Out-File -Encoding ascii '.specify\memory\n8n_webhook_url.txt'
$payload = @{ time=(Get-Date -Format 'yyyy-MM-dd HH:mm:ss'); ping='ok' } | ConvertTo-Json
try {
  Invoke-RestMethod -Method Post -Uri $wh -ContentType 'application/json' -Body $payload | Out-Null
  Write-Host "Webhook test sent to $wh" -ForegroundColor Green
} catch {
  Write-Warning "Webhook test failed: $($_.Exception.Message)"
}

Write-Host "[5/5] Run via API from 'Prepare Row'..." -ForegroundColor Cyan
