Invoke-RestMethod -Method Post -Uri ($ApiUrl.TrimEnd('/') + '/rest/workflows/run') -Headers @{ 'X-N8N-API-KEY'=$ApiKey } -ContentType 'application/json' -Body $runBody | Out-Null
Write-Host "All steps completed." -ForegroundColor Green

